  public static int Santa(String str) {
    int floor = 0;
    for (int i=0;i<str.length();i++){
      if (str.substring(i).equals(")"))
      floor = floor - 1;
      else if (str.substring(i).equals("("))
      floor = floor + 1;
    } return floor;
  }
